css:
.grid-container {
  display: grid;
  grid-template-columns: auto auto auto;
  background-color: #2196F3;
  padding: 10px;
}
.grid-item {
  background-color: rgba(255, 255, 255, 0.8);
  border: 1px solid rgba(0, 0, 0, 0.8);
  padding: 20px;
  font-size: 30px;
  text-align: center;
}


table {
  margin-left:150px;    
  text-align:center;
  border-collapse: collapse;
  width: 70%;
  border: 1px solid #ddd;
}

th, td {
  text-align: left;
  padding: 5px;
   border: 1px solid #ddd;
}

.section:not(:first-child) {
	margin-top: 1rem;
}

.section-label {
	font-size: 1.2rem;
	font-weight: 600;
}

.field + .field {
	margin-top: 0.5rem;
}

.field .label {
	font-weight: bold;
}

.field.left-right {
	display: flex;
}

.field.left-right .label {
	width: 50%;
}

.field.left-right .value {
	width: 50%;
}

.child-table [data-fieldtype="Currency"] {
	text-align: right;
}

.table-row {
	page-break-inside: avoid;
}

.page-break {
	page-break-after: always;
}

.document-header {
	border-bottom-width: 1px;
	border-bottom-style: solid;
	border-bottom-color: var(--gray-300);
}

.field[data-fieldtype="Rating"] .rating-star {
	width: 1.5rem;
}

.field[data-fieldtype="Long Text"] .value, .field[data-fieldtype="Text"] .value {
	white-space: pre-line;
}

.field[data-fieldtype="Color"] .value {
	display: flex;
	align-items: center;
}

.field[data-fieldtype="Color"] .color-square {
	width: 1rem;
	height: 1rem;
	margin-right: 0.3rem;
	border-radius: var(--border-radius);
}

.field[data-fieldtype="Check"] #icon-tick {
	width: 1rem;
}

